document.addEventListener('DOMContentLoaded', () => {    document.querySelectorAll('.accordion-header').forEach((button) => {        button.addEventListener('click', () => {            const body = button.nextElementSibling;            body.style.display = body.style.display === 'block' ? 'none' : 'block';        });    });    const filterButtons = document.querySelectorAll('[data-filter]');    const cards = document.querySelectorAll('#project-grid .card');    filterButtons.forEach((button) => {        button.addEventListener('click', () => {            const filter = button.getAttribute('data-filter');            cards.forEach((card) => {                if (filter === 'all' || card.dataset.category === filter) {                    card.style.display = 'block';                } else {                    card.style.display = 'none';                }            });        });    });});